import React, { useState } from "react";

function GerenteForm() {
  const [idGerente, setIdGerente] = useState("");
  const [nome, setNome] = useState("");
  const [cnpjHospital, setCnpjHospital] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const gerente = { idGerente, nome, cnpjHospital };

    fetch("http://localhost:8080/gerentes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(gerente),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao cadastrar gerente");
        alert("Gerente cadastrado com sucesso!");
        setIdGerente("");
        setNome("");
        setCnpjHospital("");
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Cadastrar Gerente</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>ID Gerente:</label>
          <input value={idGerente} onChange={(e) => setIdGerente(e.target.value)} />
        </div>
        <div>
          <label>Nome:</label>
          <input value={nome} onChange={(e) => setNome(e.target.value)} />
        </div>
        <div>
          <label>CNPJ do Hospital:</label>
          <input value={cnpjHospital} onChange={(e) => setCnpjHospital(e.target.value)} />
        </div>
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default GerenteForm;
