import React, { useState } from "react";

function SolicitacaoEstoqueForm() {
  const [tipo, setTipo] = useState("");
  const [quantidade, setQuantidade] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/estoque/solicitar", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ tipo, quantidade: parseInt(quantidade) }),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao solicitar bolsas");
        return res.json();
      })
      .then((data) => alert(data.message || "Solicitação realizada!"))
      .catch((err) => alert(err.message));
  };

  return (
    <div>
      <h2>Solicitar Bolsas de Sangue</h2>
      <form onSubmit={handleSubmit}>
        <label>Tipo Sanguíneo:</label>
        <input value={tipo} onChange={(e) => setTipo(e.target.value)} />
        <br />
        <label>Quantidade:</label>
        <input
          type="number"
          value={quantidade}
          onChange={(e) => setQuantidade(e.target.value)}
        />
        <br />
        <button type="submit">Solicitar</button>
      </form>
    </div>
  );
}

export default SolicitacaoEstoqueForm;
