import React, { useEffect, useState } from 'react';
import './Home.css';
import { Link } from 'react-router-dom';

function Home() {
  const [count, setCount] = useState(0);
  const [showTopBtn, setShowTopBtn] = useState(false);

  useEffect(() => {
    // contador animado
    let start = 0;
    const end = 2430;
    const duration = 2000;
    const stepTime = Math.abs(Math.floor(duration / end));
    const timer = setInterval(() => {
      start += 1;
      setCount(start);
      if (start === end) clearInterval(timer);
    }, stepTime);

    // bot√£o "voltar ao topo"
    const handleScroll = () => {
      if (window.scrollY > 300) {
        setShowTopBtn(true);
      } else {
        setShowTopBtn(false);
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => {
      clearInterval(timer);
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const scrollToSteps = () => {
    const section = document.getElementById("steps");
    section.scrollIntoView({ behavior: 'smooth' });
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <div className="home">
      {/* HERO */}
      <section className="hero animate-fadeInUp">
        <div className="hero-text">
          <h1>
            Salve vidas<br />
            doando <span className="highlight">sangue</span>
          </h1>
          <p>Um sistema completo para gest√£o de doadores, hospitais e solicita√ß√µes de sangue.</p>
          <Link to="/cadastro" className="cta-button">Solicite sangue</Link>
        </div>
      </section>

      {/* IMPACTO */}
      <section className="section impact animate-fadeInUp">
        <h2 className="section-title">Impacto gerado</h2>
        <p className="impact-counter">+{count.toLocaleString('pt-BR')} doa√ß√µes realizadas</p>
      </section>

      {/* ETAPAS */}
      <section className="section animate-fadeInUp" id="steps">
        <h2 className="section-title">Como doar sangue?</h2>
        <div className="steps">
          <div className="step">
            <div className="step-circle">1</div>
            <p>Preencha o cadastro com seus dados.</p>
          </div>
          <div className="step">
            <div className="step-circle">2</div>
            <p>Compare√ßa ao local indicado.</p>
          </div>
          <div className="step">
            <div className="step-circle">3</div>
            <p>Doe com seguran√ßa e salve vidas!</p>
          </div>
        </div>
      </section>

      {/* CURIOSIDADES */}
      <section className="section animate-fadeInUp">
        <h2 className="section-title">Curiosidades</h2>
        <div className="facts">
          <p>ü©∏ Uma doa√ß√£o pode salvar at√© 4 vidas.</p>
          <p>‚è±Ô∏è A doa√ß√£o leva em m√©dia 30 minutos.</p>
          <p>‚ù§Ô∏è Apenas 1,6% da popula√ß√£o brasileira doa sangue.</p>
        </div>
      </section>

      {/* FAQ */}
      <section className="section animate-fadeInUp">
        <h2 className="section-title">Perguntas Frequentes</h2>
        <div className="faq">
          <h3>Quem pode doar sangue?</h3>
          <p>Maiores de 16 anos com mais de 50kg e em boas condi√ß√µes de sa√∫de.</p>
          <h3>Com que frequ√™ncia posso doar?</h3>
          <p>Homens a cada 2 meses (m√°x. 4/ano), mulheres a cada 3 meses (m√°x. 3/ano).</p>
        </div>
      </section>

      {/* CTA FINAL */}
      <section className="final-cta section animate-fadeInUp">
        <h2 className="section-title">Voc√™ pode fazer a diferen√ßa hoje</h2>
        <Link to="/cadastro" className="cta-button">Torne-se um doador</Link>
      </section>

      {/* BOT√ÉO FLUTUANTE */}
      <button className="floating-button" onClick={scrollToSteps}>
        üë£ Como doar?
      </button>

      {showTopBtn && (
        <button className="back-to-top" onClick={scrollToTop}>
          ‚Üë Topo
        </button>
      )}
    </div>
  );
}

export default Home;
