import React, { useState } from "react";

function DoadorForm() {
  const [form, setForm] = useState({
    cpf: "",
    nome: "",
    idade: "",
    sexo: "",
    tipoSanguineo: "",
    enderecoRua: "",
    enderecoCep: "",
    enderecoCidade: "",
    enderecoEstado: "",
    qtdBolsasDoadas: 0
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const doador = {
      cpf: form.cpf,
      pessoa: {
        nome: form.nome,
        idade: form.idade,
        sexo: form.sexo,
        tipoSanguineo: form.tipoSanguineo,
        enderecoRua: form.enderecoRua,
        enderecoCep: form.enderecoCep,
        enderecoCidade: form.enderecoCidade,
        enderecoEstado: form.enderecoEstado
      },
      qtdBolsasDoadas: form.qtdBolsasDoadas,
    };

    fetch("http://localhost:8080/doadores", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(doador),
    })
      .then(res => res.json())
      .then(data => {
        if (data.message) {
          alert(data.message);
        }
        setForm({
          cpf: "",
          nome: "",
          idade: "",
          sexo: "",
          tipoSanguineo: "",
          enderecoRua: "",
          enderecoCep: "",
          enderecoCidade: "",
          enderecoEstado: "",
          qtdBolsasDoadas: 0
        });
      })
      .catch(err => {
        alert("Erro ao cadastrar doador: " + err.message);
      });
  };

  return (
    <div>
      <h2>Novo Doador</h2>
      <form onSubmit={handleSubmit}>
        {[
          ["cpf", "CPF"],
          ["nome", "Nome"],
          ["idade", "Idade"],
          ["sexo", "Sexo (M/F)"],
          ["tipoSanguineo", "Tipo Sanguíneo"],
          ["enderecoRua", "Rua"],
          ["enderecoCep", "CEP"],
          ["enderecoCidade", "Cidade"],
          ["enderecoEstado", "Estado"]
        ].map(([name, label]) => (
          <div key={name}>
            <label>{label}: </label>
            <input name={name} value={form[name]} onChange={handleChange} required />
          </div>
        ))}
        <button type="submit">Cadastrar</button>
      </form>
    </div>
  );
}

export default DoadorForm;
