import React, { useEffect, useState } from "react";

function SolicitacaoList() {
  const [solicitacoes, setSolicitacoes] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/solicitacoes")
      .then((res) => res.json())
      .then((data) => setSolicitacoes(data))
      .catch((err) => console.error("Erro ao buscar solicitaÃ§Ãµes", err));
  }, []);

  const handleDelete = (id) => {
    if (!window.confirm("Deseja realmente excluir esta solicitaÃ§Ã£o?")) return;

    fetch(`http://localhost:8080/solicitacoes/${id}`, {
      method: "DELETE",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao deletar");
        setSolicitacoes(solicitacoes.filter((s) => s.idSolicitacao !== id));
      })
      .catch((err) => alert(err.message));
  };

  return (
    <div style={{ padding: "30px" }}>
      <h1 style={{ textAlign: "center", color: "#c0392b" }}>ðŸ“‘ SolicitaÃ§Ãµes Registradas</h1>

      <table style={tableEstilo}>
        <thead>
          <tr>
            <th style={thEstilo}>ID</th>
            <th style={thEstilo}>Tipo SanguÃ­neo</th>
            <th style={thEstilo}>Qtd Bolsas</th>
            <th style={thEstilo}>ID Gerente</th>
            <th style={thEstilo}>Data</th>
            <th style={thEstilo}>AÃ§Ãµes</th>
          </tr>
        </thead>
        <tbody>
          {solicitacoes.map((s) => (
            <tr key={s.idSolicitacao}>
              <td style={tdEstilo}>{s.idSolicitacao}</td>
              <td style={tdEstilo}>{s.tipoSanguineo}</td>
              <td style={tdEstilo}>{s.qtdBolsasSolicitadas}</td>
              <td style={tdEstilo}>{s.idGerente}</td>
              <td style={tdEstilo}>{new Date(s.dataSolicitacao).toLocaleString()}</td>
              <td style={tdEstilo}>
                <button onClick={() => handleDelete(s.idSolicitacao)} style={deleteButton}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

const tableEstilo = { width: "100%", borderCollapse: "collapse", marginTop: "20px" };
const thEstilo = { border: "1px solid #ddd", padding: "10px", backgroundColor: "#34495e", color: "white" };
const tdEstilo = { border: "1px solid #ddd", padding: "10px" };
const deleteButton = { backgroundColor: "#e74c3c", color: "white", border: "none", padding: "6px 12px", borderRadius: "4px", cursor: "pointer" };

export default SolicitacaoList;
