import React, { useState } from "react";

function SolicitacaoForm() {
  const [idSolicitacao, setIdSolicitacao] = useState("");
  const [tipoSanguineo, setTipoSanguineo] = useState("");
  const [qtdBolsasSolicitadas, setQtdBolsasSolicitadas] = useState("");
  const [idGerente, setIdGerente] = useState("");
  const [cnpjHospital, setCnpjHospital] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const solicitacao = {
      idSolicitacao,
      tipoSanguineo,
      qtdBolsasSolicitadas,
      idGerente,
      cnpjHospital,
    };

    fetch("http://localhost:8080/solicitacoes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(solicitacao),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao cadastrar solicitação");
        alert("Solicitação cadastrada com sucesso!");
        setIdSolicitacao("");
        setTipoSanguineo("");
        setQtdBolsasSolicitadas("");
        setIdGerente("");
        setCnpjHospital("");
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Nova Solicitação</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>ID Solicitação:</label>
          <input value={idSolicitacao} onChange={(e) => setIdSolicitacao(e.target.value)} />
        </div>
        <div>
          <label>Tipo Sanguíneo:</label>
          <input value={tipoSanguineo} onChange={(e) => setTipoSanguineo(e.target.value)} />
        </div>
        <div>
          <label>Qtd Bolsas:</label>
          <input value={qtdBolsasSolicitadas} onChange={(e) => setQtdBolsasSolicitadas(e.target.value)} />
        </div>
        <div>
          <label>ID Gerente:</label>
          <input value={idGerente} onChange={(e) => setIdGerente(e.target.value)} />
        </div>
        <div>
          <label>CNPJ Hospital:</label>
          <input value={cnpjHospital} onChange={(e) => setCnpjHospital(e.target.value)} />
        </div>
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default SolicitacaoForm;
