// frontend/src/pages/doador/DoadorForm.jsx
import React, { useState } from "react";

function DoadorForm() {
  const [cpf, setCpf] = useState("");
  const [nome, setNome] = useState("");
  const [tipoSanguineo, setTipoSanguineo] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const doador = { cpf, nome, tipoSanguineo };

    fetch("http://localhost:8080/doadores", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(doador),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao cadastrar doador");
        alert("Doador cadastrado com sucesso!");
        setCpf("");
        setNome("");
        setTipoSanguineo("");
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Cadastrar Doador</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>CPF:</label>
          <input value={cpf} onChange={(e) => setCpf(e.target.value)} />
        </div>
        <div>
          <label>Nome:</label>
          <input value={nome} onChange={(e) => setNome(e.target.value)} />
        </div>
        <div>
          <label>Tipo Sangu√≠neo:</label>
          <input
            value={tipoSanguineo}
            onChange={(e) => setTipoSanguineo(e.target.value)}
          />
        </div>
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default DoadorForm;
