import React, { useEffect, useState } from "react";

function SolicitacaoList() {
  const [solicitacoes, setSolicitacoes] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/solicitacoes")
      .then((res) => res.json())
      .then((data) => setSolicitacoes(data))
      .catch((err) => console.error("Erro ao listar solicitações:", err));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:8080/solicitacoes/${id}`, {
      method: "DELETE",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao excluir solicitação");
        setSolicitacoes(solicitacoes.filter((s) => s.idSolicitacao !== id));
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Solicitações</h2>
      <ul>
        {solicitacoes.map((s) => (
          <li key={s.idSolicitacao}>
            ID: {s.idSolicitacao} | Tipo: {s.tipoSanguineo} | Qtd: {s.qtdBolsasSolicitadas} | Gerente: {s.idGerente} | Hospital: {s.cnpjHospital}
            <button onClick={() => handleDelete(s.idSolicitacao)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SolicitacaoList;
