import React, { useState } from "react";

function DoacaoForm() {
  const [cpf, setCpf] = useState("");
  const [quantidade, setQuantidade] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/doacoes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ cpf, quantidade: parseInt(quantidade) }),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao registrar doação");
        alert("Doação registrada com sucesso!");
        setCpf("");
        setQuantidade("");
      })
      .catch((err) => alert(err.message));
  };

  return (
    <div>
      <h2>Registrar Doação</h2>
      <form onSubmit={handleSubmit}>
        <label>CPF do Doador:</label>
        <input value={cpf} onChange={(e) => setCpf(e.target.value)} />
        <br />
        <label>Quantidade de Bolsas:</label>
        <input
          type="number"
          value={quantidade}
          onChange={(e) => setQuantidade(e.target.value)}
        />
        <br />
        <button type="submit">Registrar</button>
      </form>
    </div>
  );
}

export default DoacaoForm;
