import React, { useEffect, useState } from "react";

function HospitalList() {
  const [hospitais, setHospitais] = useState([]);

  useEffect(() => {
    // Ao montar o componente, buscar a lista
    fetch("http://localhost:8080/hospitais") 
      .then((res) => res.json())
      .then((data) => setHospitais(data))
      .catch((err) => console.error("Erro ao listar hospitais:", err));
  }, []);

  const handleDelete = (cnpj) => {
    fetch(`http://localhost:8080/hospitais/${cnpj}`, {
      method: "DELETE",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao deletar hospital");
        // Remove do array local
        setHospitais(hospitais.filter((h) => h.cnpj !== cnpj));
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Lista de Hospitais</h2>
      <table>
        <thead>
          <tr>
            <th>CNPJ</th>
            <th>Nome</th>
            <th>Telefone</th>
            <th>Endereço</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {hospitais.map((hosp) => (
            <tr key={hosp.cnpj}>
              <td>{hosp.cnpj}</td>
              <td>{hosp.nome}</td>
              <td>{hosp.telefone}</td>
              <td>{hosp.endereco}</td>
              <td>
                <button onClick={() => handleDelete(hosp.cnpj)}>Excluir</button>
                {/* Se quiser "Editar": <button>Editar</button> */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default HospitalList;
