import React, { useEffect, useState } from "react";

function DoadorList() {
  const [doadores, setDoadores] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/doadores")
      .then(res => res.json())
      .then(data => setDoadores(data))
      .catch(err => console.error("Erro ao listar doadores:", err));
  }, []);

  const handleDelete = (cpf) => {
    fetch(`http://localhost:3000/doadores/${cpf}`, {
      method: "DELETE"
    })
      .then(res => {
        if (!res.ok) throw new Error("Erro ao deletar");
        setDoadores(doadores.filter(d => d.cpf !== cpf));
      })
      .catch(err => alert(err));
  };

  return (
    <div>
      <h2>Listar Doadores</h2>
      <ul>
        {doadores.map(d => (
          <li key={d.cpf}>
            {d.cpf} - {d.nome} - {d.tipoSanguineo}
            <button onClick={() => handleDelete(d.cpf)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DoadorList;
