import React, { useState } from "react";

function EstoqueForm() {
  const [tipoSanguineo, setTipoSanguineo] = useState("");
  const [qtdBolsas, setQtdBolsas] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const novo = {
      tipoSanguineo,
      qtdBolsas: parseInt(qtdBolsas),
    };

    fetch("http://localhost:8080/estoque", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(novo),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao adicionar ao estoque");
        alert("Estoque atualizado!");
        setTipoSanguineo("");
        setQtdBolsas("");
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Adicionar ao Estoque</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Tipo Sangu√≠neo:</label>
          <input value={tipoSanguineo} onChange={(e) => setTipoSanguineo(e.target.value)} />
        </div>
        <div>
          <label>Quantidade de Bolsas:</label>
          <input
            type="number"
            value={qtdBolsas}
            onChange={(e) => setQtdBolsas(e.target.value)}
          />
        </div>
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default EstoqueForm;
