// frontend/src/pages/estoque/SolicitacaoEstoqueForm.jsx
import React, { useState } from "react";

function SolicitacaoEstoqueForm() {
  const [tipo, setTipo] = useState("");
  const [quantidade, setQuantidade] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/estoque/" + tipo)
      .then((res) => res.ok ? res.json() : null)
      .then((estoque) => {
        if (!estoque || estoque.qtdBolsas < parseInt(quantidade)) {
          alert("Estoque insuficiente ou tipo não encontrado.");
          return;
        }

        if (window.confirm(`Deseja confirmar a retirada de ${quantidade} bolsas do tipo ${tipo}?`)) {
          estoque.qtdBolsas -= parseInt(quantidade);
          fetch("http://localhost:8080/estoque/" + estoque.idEstoque, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(estoque),
          }).then(() => {
            alert("Retirada confirmada!");
            setTipo("");
            setQuantidade("");
          });
        }
      });
  };

  return (
    <div>
      <h2>Solicitar Bolsas de Sangue</h2>
      <form onSubmit={handleSubmit}>
        <label>Tipo Sanguíneo:</label>
        <input value={tipo} onChange={(e) => setTipo(e.target.value)} required />

        <br />

        <label>Quantidade:</label>
        <input
          type="number"
          value={quantidade}
          onChange={(e) => setQuantidade(e.target.value)}
          required
        />

        <br />
        <button type="submit">Solicitar</button>
      </form>
    </div>
  );
}

export default SolicitacaoEstoqueForm;
