// frontend/src/pages/doador/DoadorList.jsx
import React, { useState, useEffect } from "react";

function DoadorList() {
  const [doadores, setDoadores] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/doadores")
      .then((res) => res.json())
      .then((data) => setDoadores(data))
      .catch((err) => console.error("Erro ao buscar doadores:", err));
  }, []);

  const handleDelete = (cpf) => {
    fetch(`http://localhost:8080/doadores/${cpf}`, {
      method: "DELETE",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao deletar doador");
        // Remover localmente
        setDoadores(doadores.filter((d) => d.cpf !== cpf));
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Listagem de Doadores</h2>
      <table>
        <thead>
          <tr>
            <th>CPF</th>
            <th>Nome</th>
            <th>Tipo</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {doadores.map((d) => (
            <tr key={d.cpf}>
              <td>{d.cpf}</td>
              <td>{d.nome}</td>
              <td>{d.tipoSanguineo}</td>
              <td>
                <button onClick={() => handleDelete(d.cpf)}>Excluir</button>
                {/* 
                  Se quiser um botão "Editar" que vai a DoadorForm
                  ex: <button>Editar</button> 
                */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default DoadorList;
