import React, { useState } from "react";

function BolsaForm() {
  const [tipoSanguineo, setTipoSanguineo] = useState("");
  const [cpfDoador, setCpfDoador] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    const bolsa = { tipoSanguineo, cpfDoador };

    fetch("http://localhost:8080/bolsas", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(bolsa),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao cadastrar bolsa");
        alert("Bolsa registrada com sucesso!");
        setTipoSanguineo("");
        setCpfDoador("");
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Registrar Bolsa de Sangue</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Tipo Sangu√≠neo:</label>
          <input value={tipoSanguineo} onChange={(e) => setTipoSanguineo(e.target.value)} />
        </div>
        <div>
          <label>CPF do Doador:</label>
          <input value={cpfDoador} onChange={(e) => setCpfDoador(e.target.value)} />
        </div>
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default BolsaForm;
