import React, { useState } from "react";

function DoacaoForm() {
  const [cpf, setCpf] = useState("");
  const [quantidade, setQuantidade] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch(`http://localhost:8080/doacao?cpf=${cpf}&quantidade=${quantidade}`, {
      method: "POST",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao registrar doação");
        alert("Doação registrada com sucesso!");
        setCpf("");
        setQuantidade("");
      })
      .catch((err) => alert(err.message));
  };

  return (
    <div>
      <h2>Registrar Doação de Sangue</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>CPF do Doador:</label>
          <input
            value={cpf}
            onChange={(e) => setCpf(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Quantidade de Bolsas:</label>
          <input
            type="number"
            value={quantidade}
            onChange={(e) => setQuantidade(e.target.value)}
            required
          />
        </div>
        <button type="submit">Registrar</button>
      </form>
    </div>
  );
}

export default DoacaoForm;
