// src/pages/solicitacao/SolicitacaoForm.js
import React, { useState } from "react";

function SolicitacaoForm() {
  const [form, setForm] = useState({
    tipoSanguineo: "",
    qtdBolsasSolicitadas: "",
    idGerente: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const solicitacao = {
      // Gera um id único para a solicitação, por exemplo
      idSolicitacao: "SOL-" + Date.now(),
      tipoSanguineo: form.tipoSanguineo,
      qtdBolsasSolicitadas: parseInt(form.qtdBolsasSolicitadas, 10),
      idGerente: form.idGerente
    };

    try {
      const response = await fetch("http://localhost:8080/solicitacoes", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(solicitacao)
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.error || "Erro ao registrar solicitação");
      }
      alert(data.message || "Solicitação registrada com sucesso!");
      setForm({
        tipoSanguineo: "",
        qtdBolsasSolicitadas: "",
        idGerente: ""
      });
    } catch (err) {
      alert("Erro: " + err.message);
    }
  };

  return (
    <div>
      <h2>Solicitar Bolsas do Estoque</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Tipo Sanguíneo:</label>
          <input
            type="text"
            name="tipoSanguineo"
            value={form.tipoSanguineo}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Quantidade de Bolsas:</label>
          <input
            type="number"
            name="qtdBolsasSolicitadas"
            value={form.qtdBolsasSolicitadas}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>ID Gerente:</label>
          <input
            type="text"
            name="idGerente"
            value={form.idGerente}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Solicitar</button>
      </form>
    </div>
  );
}

export default SolicitacaoForm;
