import React, { useEffect, useState } from "react";

function BolsaList() {
  const [bolsas, setBolsas] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/bolsas")
      .then((res) => res.json())
      .then((data) => setBolsas(data))
      .catch((err) => console.error("Erro ao buscar bolsas:", err));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:8080/bolsas/${id}`, {
      method: "DELETE",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao deletar bolsa");
        setBolsas(bolsas.filter((b) => b.idBolsa !== id));
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Lista de Bolsas de Sangue</h2>
      <ul>
        {bolsas.map((b) => (
          <li key={b.idBolsa}>
            ID: {b.idBolsa} | Tipo: {b.tipoSanguineo} | Doador: {b.cpfDoador}
            <button onClick={() => handleDelete(b.idBolsa)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default BolsaList;
