import React, { useState } from "react";

function DoadorForm() {
  const [form, setForm] = useState({
    cpf: "",
    nome: "",
    idade: "",
    sexo: "",
    tipoSanguineo: "",
    rua: "",
    cep: "",
    cidade: "",
    estado: ""
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/doadores", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form)
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao cadastrar doador");
        alert("Doador cadastrado com sucesso!");
        setForm({
          cpf: "",
          nome: "",
          idade: "",
          sexo: "",
          tipoSanguineo: "",
          rua: "",
          cep: "",
          cidade: "",
          estado: ""
        });
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Cadastro de Doador</h2>
      <form onSubmit={handleSubmit}>
        {["cpf", "nome", "idade", "sexo", "tipoSanguineo", "rua", "cep", "cidade", "estado"].map((field) => (
          <div key={field}>
            <label>{field}: </label>
            <input name={field} value={form[field]} onChange={handleChange} />
          </div>
        ))}
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default DoadorForm;
