import React, { useState } from "react";

function SolicitacaoForm() {
  const [tipoSanguineo, setTipoSanguineo] = useState("");
  const [qtdBolsasSolicitadas, setQtdBolsasSolicitadas] = useState("");
  const [idGerente, setIdGerente] = useState("");
  const [cnpjHospital, setCnpjHospital] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    const solicitacao = {
      tipoSanguineo,
      qtdBolsasSolicitadas: parseInt(qtdBolsasSolicitadas),
      idGerente: parseInt(idGerente),
      cnpjHospital,
    };

    fetch("http://localhost:8080/solicitacoes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(solicitacao),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao criar solicitação");
        alert("Solicitação registrada com sucesso!");
        setTipoSanguineo("");
        setQtdBolsasSolicitadas("");
        setIdGerente("");
        setCnpjHospital("");
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Nova Solicitação de Bolsas</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Tipo Sanguíneo:</label>
          <input value={tipoSanguineo} onChange={(e) => setTipoSanguineo(e.target.value)} />
        </div>
        <div>
          <label>Quantidade:</label>
          <input type="number" value={qtdBolsasSolicitadas} onChange={(e) => setQtdBolsasSolicitadas(e.target.value)} />
        </div>
        <div>
          <label>ID do Gerente:</label>
          <input type="number" value={idGerente} onChange={(e) => setIdGerente(e.target.value)} />
        </div>
        <div>
          <label>CNPJ do Hospital:</label>
          <input value={cnpjHospital} onChange={(e) => setCnpjHospital(e.target.value)} />
        </div>
        <button type="submit">Solicitar</button>
      </form>
    </div>
  );
}

export default SolicitacaoForm;
