import React, { useState } from "react";

function SolicitacaoEstoqueForm() {
  const [tipo, setTipo] = useState("");
  const [qtd, setQtd] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/estoque/solicitar", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ tipo, qtd }),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao solicitar bolsas");
        return res.json();
      })
      .then((data) => {
        alert(data.message || "Solicitação realizada!");
        setTipo("");
        setQtd("");
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Solicitar Bolsas do Estoque</h2>
      <form onSubmit={handleSubmit}>
        <label>Tipo Sanguíneo:</label>
        <input value={tipo} onChange={(e) => setTipo(e.target.value)} required />
        <br />
        <label>Quantidade Desejada:</label>
        <input
          type="number"
          value={qtd}
          onChange={(e) => setQtd(e.target.value)}
          required
        />
        <br />
        <button type="submit">Solicitar</button>
      </form>
    </div>
  );
}

export default SolicitacaoEstoqueForm;
