import React, { useState } from "react";

function GerenteForm() {
  const [nome, setNome] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const gerente = { nome };

    fetch("http://localhost:8080/gerentes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(gerente),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao cadastrar gerente");
        return res.json().catch(() => ({}));
      })
      .then(() => {
        alert("Gerente cadastrado com sucesso!");
        setNome("");
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Novo Gerente</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nome do Gerente:</label>
          <input value={nome} onChange={(e) => setNome(e.target.value)} />
        </div>
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default GerenteForm;
