// src/pages/sistema/Sistema.jsx
import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import Chart from "chart.js/auto";
import "./Sistema.css";

function Sistema() {
  useEffect(() => {
    let chart1, chart2, chart3;

    // Destroi qualquer gráfico anterior (evita erro de canvas duplicado)
    if (Chart.getChart("bloodChart")) Chart.getChart("bloodChart").destroy();
    if (Chart.getChart("doadoresChart")) Chart.getChart("doadoresChart").destroy();
    if (Chart.getChart("doacoesMensaisChart")) Chart.getChart("doacoesMensaisChart").destroy();

    // Gráfico de Barras - Estoque por Tipo Sanguíneo
    chart1 = new Chart(document.getElementById("bloodChart"), {
      type: "bar",
      data: {
        labels: ["A+", "A−", "B+", "B−", "AB+", "AB−", "O+", "O−"],
        datasets: [{
          label: "Bolsas em Estoque",
          data: [23, 10, 15, 5, 8, 4, 30, 12],
          backgroundColor: "#7B1E1E"
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: { display: false }
        },
        scales: {
          y: {
            beginAtZero: true,
            ticks: { stepSize: 5 }
          }
        }
      }
    });

    // Gráfico Doughnut - Perfil dos Doadores
    chart2 = new Chart(document.getElementById("doadoresChart"), {
      type: "doughnut",
      data: {
        labels: ["Homens", "Mulheres", "Outros"],
        datasets: [{
          data: [180, 120, 15],
          backgroundColor: ["#ec3e67", "#478eff", "#aaa"]
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: { position: "bottom" }
        }
      }
    });

    // Gráfico de Linha - Doações Mensais
    chart3 = new Chart(document.getElementById("doacoesMensaisChart"), {
      type: "line",
      data: {
        labels: ["Jan", "Fev", "Mar", "Abr", "Mai", "Jun", "Jul", "Ago"],
        datasets: [{
          label: "Doações realizadas",
          data: [120, 135, 150, 170, 200, 210, 190, 230],
          fill: true,
          borderColor: "#478eff",
          backgroundColor: "rgba(71, 142, 255, 0.1)",
          tension: 0.3
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: { position: "top" }
        },
        scales: {
          y: { beginAtZero: true }
        }
      }
    });

    // Destrói os gráficos ao desmontar o componente
    return () => {
      chart1?.destroy();
      chart2?.destroy();
      chart3?.destroy();
    };
  }, []);

  return (
    <div className="sistema-container">
      <h1 className="sistema-header">Painel do Banco de Sangue</h1>

      <div className="sistema-grid">
        <div className="sistema-card">
          <h3>👤 Doadores</h3>
          <Link to="/doadores">📋 Listar Doadores</Link>
        </div>
        <div className="sistema-card">
          <h3>🏥 Hospitais</h3>
          <Link to="/hospitais">📋 Listar Hospitais</Link><br />
          <Link to="/hospitais/novo">➕ Cadastrar Hospital</Link>
        </div>
        <div className="sistema-card">
          <h3>🧑‍💼 Gerentes</h3>
          <Link to="/gerentes">📋 Listar Gerentes</Link><br />
          <Link to="/gerentes/novo">➕ Cadastrar Gerente</Link>
        </div>
        <div className="sistema-card">
          <h3>📑 Solicitações</h3>
          <Link to="/solicitacoes">📋 Listar Solicitações</Link><br />
          <Link to="/solicitacoes/novo">➕ Solicitar Bolsas</Link>
        </div>
        <div className="sistema-card">
          <h3>📦 Estoque</h3>
          <Link to="/estoque">📋 Ver Estoque</Link><br />
          <Link to="/estoque/novo">➕ Adicionar Estoque</Link>
        </div>
        <div className="sistema-card">
          <h3>⚙️ Funcionalidades</h3>
          <Link to="/doacoes">✅ Registrar Doação</Link>
        </div>
      </div>

      {/* GRÁFICOS */}
      <div className="sistema-analytics">
        <h2 className="sistema-subheader">Estatísticas do Sistema</h2>
        <div className="sistema-charts">
          <div className="chart-card">
            <h3>Estoque por Tipo Sanguíneo</h3>
            <canvas id="bloodChart"></canvas>
          </div>
          <div className="chart-card">
            <h3>Perfil dos Doadores</h3>
            <canvas id="doadoresChart"></canvas>
          </div>
          <div className="chart-card">
            <h3>Doações Mensais</h3>
            <canvas id="doacoesMensaisChart"></canvas>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Sistema;
