import React, { useState } from "react";

function DoacaoForm() {
  const [cpf, setCpf] = useState("");
  const [qtd, setQtd] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/doacoes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ cpf, qtd }),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao registrar doação");
        alert("Doação registrada com sucesso!");
        setCpf("");
        setQtd("");
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Registrar Doação</h2>
      <form onSubmit={handleSubmit}>
        <label>CPF do Doador:</label>
        <input value={cpf} onChange={(e) => setCpf(e.target.value)} required />
        <br />
        <label>Quantidade de Bolsas:</label>
        <input
          type="number"
          value={qtd}
          onChange={(e) => setQtd(e.target.value)}
          required
        />
        <br />
        <button type="submit">Confirmar Doação</button>
      </form>
    </div>
  );
}

export default DoacaoForm;
