import React, { useEffect, useState } from "react";

function SolicitacaoList() {
  const [solicitacoes, setSolicitacoes] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/solicitacoes")
      .then(res => res.json())
      .then(data => setSolicitacoes(data))
      .catch(err => console.error("Erro ao buscar solicitações", err));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:8080/solicitacoes/${id}`, {
      method: "DELETE",
    })
      .then(res => {
        if (!res.ok) throw new Error("Erro ao deletar");
        setSolicitacoes(solicitacoes.filter(s => s.idSolicitacao !== id));
      })
      .catch(err => alert(err.message));
  };

  return (
    <div>
      <h2>Solicitações Registradas</h2>
      <ul>
        {solicitacoes.map(s => (
          <li key={s.idSolicitacao}>
            <strong>ID:</strong> {s.idSolicitacao} | <strong>Tipo:</strong> {s.tipoSanguineo} | <strong>Qtd:</strong> {s.qtdBolsasSolicitadas} | <strong>Gerente:</strong> {s.idGerente}
            <button onClick={() => handleDelete(s.idSolicitacao)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SolicitacaoList;
