import React, { useState } from "react";

function BolsaForm() {
  const [idBolsa, setIdBolsa] = useState("");
  const [tipoSanguineo, setTipoSanguineo] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    const bolsa = { idBolsa, tipoSanguineo };

    fetch("http://localhost:8080/bolsas", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(bolsa),
    })
      .then(res => {
        if (!res.ok) throw new Error("Erro ao criar bolsa");
        alert("Bolsa registrada com sucesso!");
        setIdBolsa("");
        setTipoSanguineo("");
      })
      .catch(err => alert(err.message));
  };

  return (
    <div>
      <h2>Cadastrar Bolsa de Sangue</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>ID da Bolsa:</label>
          <input value={idBolsa} onChange={(e) => setIdBolsa(e.target.value)} required />
        </div>
        <div>
          <label>Tipo Sangu√≠neo:</label>
          <input value={tipoSanguineo} onChange={(e) => setTipoSanguineo(e.target.value)} required />
        </div>
        <button type="submit">Registrar</button>
      </form>
    </div>
  );
}

export default BolsaForm;
