import React, { useEffect, useState } from "react";

function HospitalList() {
  const [hospitais, setHospitais] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/hospitais")
      .then((res) => res.json())
      .then((data) => setHospitais(data))
      .catch((err) => console.error(err));
  }, []);

  const handleDelete = (cnpj) => {
    fetch(`http://localhost:8080/hospitais/${cnpj}`, { method: "DELETE" })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao deletar hospital");
        setHospitais((prev) => prev.filter((h) => h.cnpj !== cnpj));
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Lista de Hospitais</h2>
      <ul>
        {hospitais.map((h) => (
          <li key={h.cnpj}>
            {h.cnpj} - {h.nome} <button onClick={() => handleDelete(h.cnpj)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default HospitalList;
