import React, { useEffect, useState } from "react";

function EstoqueList() {
  const [estoques, setEstoques] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/estoque")
      .then(res => res.json())
      .then(data => setEstoques(data))
      .catch(err => console.error("Erro ao listar estoque", err));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:8080/estoque/${id}`, {
      method: "DELETE",
    })
      .then(res => {
        if (!res.ok) throw new Error("Erro ao deletar estoque");
        setEstoques(estoques.filter(e => e.idEstoque !== id));
      })
      .catch(err => alert(err.message));
  };

  return (
    <div>
      <h2>Estoque de Bolsas de Sangue</h2>
      <ul>
        {estoques.map((e) => (
          <li key={e.idEstoque}>
            <strong>ID:</strong> {e.idEstoque} | <strong>Tipo:</strong> {e.tipoSanguineo} |{" "}
            <strong>Quantidade:</strong> {e.qtdBolsas}
            <button onClick={() => handleDelete(e.idEstoque)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default EstoqueList;
