// src/pages/DoadorForm.jsx
import React, { useState } from "react";

function DoadorForm() {
  const [cpf, setCpf] = useState("");
  const [nome, setNome] = useState("");
  const [tipoSangue, setTipoSangue] = useState("");
  // Acrescente outros campos se você tiver (idade, sexo, etc.)

  const handleSubmit = (e) => {
    e.preventDefault();
    const doador = {
      cpf,
      nome,
      tipoSanguineo: tipoSangue,
      // ... outros campos
    };

    fetch("http://localhost:8080/doadores", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(doador),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao cadastrar doador");
        return res.json().catch(() => ({}));
      })
      .then(() => {
        alert("Doador cadastrado com sucesso!");
        setCpf("");
        setNome("");
        setTipoSangue("");
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Cadastrar Doador</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>CPF:</label>
          <input value={cpf} onChange={(e) => setCpf(e.target.value)} />
        </div>
        <div>
          <label>Nome:</label>
          <input value={nome} onChange={(e) => setNome(e.target.value)} />
        </div>
        <div>
          <label>Tipo Sanguíneo:</label>
          <input
            value={tipoSangue}
            onChange={(e) => setTipoSangue(e.target.value)}
          />
        </div>
        {/* Se quiser outros campos, crie mais inputs */}
        <button type="submit">Salvar</button>
      </form>
    </div>
  );
}

export default DoadorForm;
