import React, { useEffect, useState } from "react";

function GerenteList() {
  const [gerentes, setGerentes] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/gerentes")
      .then(res => res.json())
      .then(data => setGerentes(data))
      .catch(err => console.error("Erro ao listar gerentes:", err));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:8080/gerentes/${id}`, {
      method: "DELETE",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao excluir gerente");
        setGerentes(gerentes.filter((g) => g.idGerente !== id));
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Lista de Gerentes</h2>
      <ul>
        {gerentes.map((g) => (
          <li key={g.idGerente}>
            {g.idGerente} - {g.nome} - CNPJ Hospital: {g.cnpjHospital}
            <button onClick={() => handleDelete(g.idGerente)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default GerenteList;
