import React, { useState } from "react";

function SolicitacaoEstoqueForm() {
  const [tipoSanguineo, setTipoSanguineo] = useState("");
  const [quantidade, setQuantidade] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch(`http://localhost:8080/estoque/solicitar?tipo=${tipoSanguineo}&quantidade=${quantidade}`, {
      method: "POST",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao solicitar bolsas");
        alert("Solicitação de bolsas realizada com sucesso!");
        setTipoSanguineo("");
        setQuantidade("");
      })
      .catch((err) => alert(err.message));
  };

  return (
    <div>
      <h2>Solicitar Bolsas do Estoque</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Tipo Sanguíneo:</label>
          <input
            value={tipoSanguineo}
            onChange={(e) => setTipoSanguineo(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Quantidade:</label>
          <input
            type="number"
            value={quantidade}
            onChange={(e) => setQuantidade(e.target.value)}
            required
          />
        </div>
        <button type="submit">Solicitar</button>
      </form>
    </div>
  );
}

export default SolicitacaoEstoqueForm;
