import React, { useEffect, useState } from "react";

function EstoqueList() {
  const [estoques, setEstoques] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/estoque")
      .then((res) => res.json())
      .then((data) => setEstoques(data))
      .catch((err) => console.error("Erro ao buscar estoque:", err));
  }, []);

  const handleDelete = (idEstoque) => {
    fetch(`http://localhost:8080/estoque/${idEstoque}`, {
      method: "DELETE",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao deletar item do estoque");
        setEstoques(estoques.filter((e) => e.idEstoque !== idEstoque));
      })
      .catch((err) => alert(err));
  };

  return (
    <div>
      <h2>Estoque de Bolsas</h2>
      <ul>
        {estoques.map((e) => (
          <li key={e.idEstoque}>
            Tipo: {e.tipoSanguineo} | Quantidade: {e.qtdBolsas}
            <button onClick={() => handleDelete(e.idEstoque)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default EstoqueList;
