import React from 'react';
import {
  AppBar, Toolbar, Typography, Button, Box, Container, Grid, Paper
} from '@mui/material';
import { Link } from 'react-router-dom';

function Home() {
  return (
    <>
      {/* NAVBAR */}
      <AppBar position="static" sx={{ backgroundColor: '#ffffff', color: '#000', boxShadow: 2 }}>
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            ü©∏ Banco de Sangue
          </Typography>
          <Button component={Link} to="/" color="inherit">In√≠cio</Button>
          <Button component={Link} to="/sobre" color="inherit">Sobre N√≥s</Button>
          <Button component={Link} to="/cadastro" color="inherit">Se Cadastre</Button>
          <Button component={Link} to="/login" color="inherit" variant="outlined" sx={{ ml: 2 }}>Log In</Button>
        </Toolbar>
      </AppBar>

      {/* HERO */}
      <Box sx={{ minHeight: '80vh', background: 'linear-gradient(to right, #7B1E1E 30%, #fff 30%)', display: 'flex', alignItems: 'center' }}>
        <Container>
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} md={6}>
              {/* Lado vazio / arte curva */}
              <Box sx={{ display: { xs: 'none', md: 'block' }, height: '100%', backgroundColor: '#7B1E1E' }} />
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography variant="h3" fontWeight="bold" gutterBottom>
                Salve vidas<br />doando sangue
              </Typography>
              <Typography variant="h6" color="text.secondary" gutterBottom>
                Um sistema completo para gest√£o de doadores, hospitais e solicita√ß√µes de sangue.
              </Typography>
              <Button
                variant="contained"
                sx={{
                  mt: 4,
                  backgroundColor: '#000',
                  color: '#fff',
                  px: 4,
                  py: 1.5,
                  fontWeight: 'bold',
                  '&:hover': {
                    backgroundColor: '#222'
                  }
                }}
                component={Link}
                to="/cadastro"
              >
                Solicite sangue
              </Button>
            </Grid>
          </Grid>
        </Container>
      </Box>
    </>
  );
}

export default Home;
