// src/pages/hospital/HospitalList.jsx

import React, { useEffect, useState } from "react";

function HospitalList() {
  const [hospitais, setHospitais] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/hospitais")
      .then((res) => res.json())
      .then((data) => setHospitais(data))
      .catch((err) => console.error("Erro ao listar hospitais:", err));
  }, []);

  const handleDelete = (cnpj) => {
    if (!window.confirm("Tem certeza que deseja excluir este hospital?")) return;

    fetch(`http://localhost:8080/hospitais/${cnpj}`, {
      method: "DELETE",
    })
      .then((res) => {
        if (!res.ok) throw new Error("Erro ao deletar hospital");
        setHospitais(hospitais.filter((h) => h.cnpj !== cnpj));
      })
      .catch((err) => alert(err.message));
  };

  return (
    <div style={{ padding: "30px", backgroundColor: "#f7f7f7", minHeight: "100vh" }}>
      <h2 style={{ textAlign: "center", color: "#2980b9", marginBottom: "30px" }}>üè• Hospitais Cadastrados</h2>

      <div style={{ overflowX: "auto" }}>
        <table style={{ width: "100%", borderCollapse: "collapse", backgroundColor: "#fff", boxShadow: "0 2px 8px rgba(0,0,0,0.1)" }}>
          <thead>
            <tr style={{ backgroundColor: "#2980b9", color: "white" }}>
              <th style={thStyle}>CNPJ</th>
              <th style={thStyle}>Nome</th>
              <th style={thStyle}>Telefone</th>
              <th style={thStyle}>Endere√ßo</th>
              <th style={thStyle}>A√ß√µes</th>
            </tr>
          </thead>
          <tbody>
            {hospitais.map((hospital) => (
              <tr key={hospital.cnpj}>
                <td style={tdStyle}>{hospital.cnpj}</td>
                <td style={tdStyle}>{hospital.nome}</td>
                <td style={tdStyle}>{hospital.telefone}</td>
                <td style={tdStyle}>{hospital.endereco}</td>
                <td style={tdStyle}>
                  <button onClick={() => handleDelete(hospital.cnpj)} style={buttonStyle}>
                    Excluir
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

const thStyle = {
  padding: "12px",
  textAlign: "left",
  borderBottom: "2px solid #ddd",
};

const tdStyle = {
  padding: "10px",
  borderBottom: "1px solid #eee",
};

const buttonStyle = {
  backgroundColor: "#e74c3c",
  color: "white",
  border: "none",
  padding: "8px 16px",
  borderRadius: "6px",
  cursor: "pointer",
};

export default HospitalList;
